import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";

const BarChart = (prop) => {
	const [pokemonChartDetail, setPokemonChartDetail] = useState();
	console.log(pokemonChartDetail);
	return (
		<div>
			<Bar
				data={{ pokemonChartDetail }}
				height={200}
				width={600}
				options={{
					maintainAspectRatio: false,
					scales: {
						yAxes: [
							{
								ticks: {
									beginAtZero: true,
								},
							},
						],
					},
					legend: {
						labels: {
							fontSize: 25,
						},
					},
				}}
			/>
		</div>
	);
};

export default BarChart;
