import "./App.css";
import { useState, useEffect } from "react";
import ReactLoading from "react-loading";
import axios from "axios";
import { useFetch } from "./Hooks/useFetch";
import Modal from "react-modal";
import PokemonThumb from "./components/PokemonThumb";
import { FcSearch } from "react-icons/fc";
import { GrPowerReset } from "react-icons/gr";

function PokeDex() {
	// const [allPokemons, setAllPokemons] = useState([]);
	const [pokemons, setPokemons] = useState([]);
	const [pokemonDetail, setPokemonDetail] = useState(null);
	const [isLoading, setIsLoading] = useState(false);
	const [error, setError] = useState(null);
	const [value, setValue] = useState("");
	const url = "https://pokeapi.co/api/v2/pokemon?limit=20";

	// const { data, isLoading } = useFetch("https://pokeapi.co/api/v2/pokemon");

	// useEffect(() => {
	// 	setPokemons(data);
	// 	console.log(pokemons);
	// }, [pokemons]);

	// useEffect(() => {
	// 	setIsLoading(true);
	// 	axios
	// 		.get(url)
	// 		.then((response) => {
	// 			setPokemons(response.data);
	// 		})
	// 		.catch((err) => {
	// 			setError(err);
	// 			console.log(error);
	// 		})
	// 		.finally(() => {
	// 			setIsLoading(false);
	// 		});
	// }, [url]);

	// useEffect(() => {
	// 	const fetchPokemon = async () => {
	// 		try {
	// 			setIsLoading(true);
	// 			const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
	// 			console.log(response);
	// 			setPokemons(response.data);
	// 		} catch (err) {
	// 			if (err.response) {
	// 				// Not in the 200 response range
	// 				console.log(err.response.data);
	// 				console.log(err.response.status);
	// 				console.log(err.response.headers);
	// 			} else {
	// 				// If we dont get a response at all
	// 				console.log(`Error: ${err.message}`);
	// 			}
	// 		}
	// 	};

	// 	fetchPokemon();
	// 	setIsLoading(false);
	// }, []);
	const getAllPokemons = async () => {
		setIsLoading(true);
		const response = await axios.get(url);
		const data = response.data;

		function createPokemonObject(results) {
			results.forEach(async (pokemon) => {
				const response = await axios.get(
					`https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
				);
				const data = response.data;
				setPokemons((currentList) => [...currentList, data]);
			});
		}
		createPokemonObject(data.results);
	};

	useEffect(() => {
		getAllPokemons();
		setIsLoading(false);
	}, []);

	// const sorting = () => {
	// 	pokemons.sort((a, b) => a.id - b.id);
	// };

	const sorting2 = (a, b) => {
		if (a.name < b.name) {
			return -1;
		}
		if (a.name > b.name) {
			return 1;
		}
		return 0;
	};
	const handleSearch = async (e) => {
		e.preventDefault();
		return await axios
			.get(`https://pokeapi.co/api/v2/pokemon/?q=${value}`)
			.then((response) => {
				setPokemons(response.data.results);
				setValue("");
			})
			.catch((err) => console.log(err));
	};
	const handleReset = () => {
		getAllPokemons();
	};

	pokemons.sort((a, b) => a.id - b.id);
	// pokemons.sort(sorting2);

	console.log(pokemons);

	const customStyles = {
		content: {
			top: "50%",
			left: "50%",
			right: "auto",
			bottom: "auto",
			marginRight: "-50%",
			transform: "translate(-50%, -50%)",
			background: "black",
			color: "white",
		},
		overlay: { backgroundColor: "grey" },
	};

	if (!isLoading && pokemons.length === 0) {
		return (
			<div>
				<header className="App-header">
					<h1>Welcome to pokedex !</h1>
					<h2>Requirement:</h2>
					<ul>
						<li>
							Call this api:https://pokeapi.co/api/v2/pokemon to get pokedex,
							and show a list of pokemon name.
						</li>
						<li>Implement React Loading and show it during API call</li>
						<li>
							when hover on the list item , change the item color to yellow.
						</li>
						<li>when clicked the list item, show the modal below</li>
						<li>
							Add a search bar on top of the bar for searching, search will run
							on keyup event
						</li>
						<li>Implement sorting and pagingation</li>
						<li>Commit your codes after done</li>
						<li>
							If you do more than expected (E.g redesign the page / create a
							chat feature at the bottom right). it would be good.
						</li>
					</ul>
				</header>
			</div>
		);
	}

	return (
		<div>
			<header className="App-header">
				{isLoading ? (
					<>
						<div className="App">
							<header className="App-header">
								<b>Implement loader here</b>
							</header>
						</div>
					</>
				) : (
					<div className="pokemon-container">
						<h1>Welcome to pokedex !</h1>
						<form
							// style={{
							// 	margin: "auto",
							// 	padding: "15px",
							// 	maxWidth: "400px",
							// 	alignContent: "center",
							// }}
							className="form"
							onSubmit={handleSearch}
						>
							<input
								type="text"
								className="form__input"
								placeholder="Search pokemon"
								value={value}
								onChange={(e) => setValue(e.target.value)}
							/>
							<FcSearch />
						</form>
						<button onClick={() => handleReset()}>
							<GrPowerReset />
						</button>
						<div className="all-container">
							{pokemons.map((pokemonStats, index) => (
								<PokemonThumb
									key={index}
									id={pokemonStats.id}
									// image={pokemonStats.sprites.other.dream_world.front_default}
									name={pokemonStats.name}
									type={pokemonStats.types[0].type.name}
								/>
							))}
						</div>
						{/* <button onClick={sorting}>Sort me by ID</button>
						<button onClick={() => pokemons.sort(sorting2)}>
							Sort me by alphabatical
						</button> */}
					</div>
				)}
			</header>
			{pokemonDetail && (
				<Modal
					isOpen={pokemonDetail}
					contentLabel={pokemonDetail?.name || ""}
					onRequestClose={() => {
						setPokemonDetail(null);
					}}
					style={customStyles}
				>
					<div>
						Requirement:
						<ul>
							<li>show the sprites front_default as the pokemon image</li>
							<li>
								Show the stats details - only stat.name and base_stat is
								required in tabular format
							</li>
							<li>Create a bar chart based on the stats above</li>
							<li>
								Create a button to download the information generated in this
								modal as pdf. (images and chart must be included)
							</li>
						</ul>
					</div>
				</Modal>
			)}
		</div>
	);
}

export default PokeDex;
