import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";

const BarChart = (prop) => {
	const [pokemonChartDetail, setPokemonChartDetail] = useState({
		labels: prop.stats.map((stat) => stat.stat.name),
		datasets: [
			{
				label: "Pokemon stats",
				data: prop.stats.map((stat) => stat.base_stat),
				backgroundColor: "#282c34",
				borderColor: "black",
				borderWidth: 2,
			},
		],
	});
	console.log(pokemonChartDetail);
	return (
		<div>
			<Bar
				data={{ pokemonChartDetail }}
				height={200}
				width={600}
				options={{
					maintainAspectRatio: false,
					scales: {
						yAxes: [
							{
								ticks: {
									beginAtZero: true,
								},
							},
						],
					},
					legend: {
						labels: {
							fontSize: 25,
						},
					},
				}}
			/>
		</div>
	);
};

export default BarChart;
