import React from "react";
import {
	Page,
	Text,
	Image,
	Document,
	StyleSheet,
	Font,
	View,
} from "@react-pdf/renderer";
import BarChart from "./BarChart";
import { Chart as ChartJS } from "chart.js/auto";

Font.register({
	family: "Oswald",
	src: "https://fonts.gstatic.com/s/oswald/v13/Y_TKV6o8WovbUd3m_X9aAA.ttf",
});

const styles = StyleSheet.create({
	body: {
		paddingTop: 35,
		paddingBottom: 65,
		paddingHorizontal: 35,
	},
	title: {
		fontSize: 24,
		textAlign: "center",
		fontFamily: "Oswald",
	},
	author: {
		fontSize: 12,
		textAlign: "center",
		marginBottom: 40,
	},
	subtitle: {
		fontSize: 18,
		margin: 12,
		fontFamily: "Oswald",
	},
	text: {
		margin: 12,
		fontSize: 14,
		textAlign: "justify",
		fontFamily: "Times-Roman",
		display: "flex",
		flexDirection: "column",
	},
	image: {
		marginHorizontal: 100,
		// width: 500,
		// height: 250,
	},
	header: {
		fontSize: 12,
		marginBottom: 20,
		textAlign: "center",
		color: "grey",
	},
	pageNumber: {
		position: "absolute",
		fontSize: 12,
		bottom: 30,
		left: 0,
		right: 0,
		textAlign: "center",
		color: "grey",
	},
	flex: {
		display: "flex",
		justifyContent: "center",
		alignItems: "center",
	},
});

const PDFFile = ({ prop }) => {
	console.log(prop);
	return (
		<Document>
			<Page style={styles.body}>
				<Text style={styles.header} fixed>
					~ Created with react-pdf ~
				</Text>
				<Text style={styles.title}>{props[0].name}</Text>
				<Image
					style={styles.image}
					src={prop[0].sprites.other.home.front_default}
				/>
				<Text style={styles.author}>Pokemon Stats</Text>
				<View style={styles.flex}>
					<Text style={styles.text}>
						<View>
							{prop[0].stats.map(
								(stat) => stat.stat.name + " => " + stat.base_stat + ", "
							)}
						</View>
					</Text>
					{/* <Text style={styles.text}>
						{prop[0].stats.map((stat) => stat.base_stat)}
					</Text> */}
				</View>

				{/* Bawah ni code dia */}
				{/* <Svg
					style={styles.image}
					src={prop[0].sprites.other.dream_world.front_default}
				/> */}
				{/* Below direct Image URL from API pon x leh nak muncul dalam pdf tapi dekat homapage sume gambar tu load je */}
				{/* <BarChart prop={prop} /> */}

				<Text style={styles.subtitle}>Collected from PokeAPI</Text>

				<Text
					style={styles.pageNumber}
					render={({ pageNumber, totalPages }) =>
						`${pageNumber} / ${totalPages}`
					}
					fixed
				/>
			</Page>
		</Document>
	);
};

export default PDFFile;
