import "./App.css";
import { useState, useEffect } from "react";
import ReactLoading from "react-loading";
import axios from "axios";
import Modal from "react-modal";
import PokemonThumb from "./components/PokemonThumb";
import ReactPaginate from "react-paginate";
import { HiArrowSmLeft, HiArrowSmRight } from "react-icons/hi";
import Navbar from "./components/Navbar";
import { FaTimes } from "react-icons/fa";
import ProgressBar from "react-bootstrap/ProgressBar";
import BarChart from "./components/BarChart";

function PokeDex() {
	const [pokemons, setPokemons] = useState([]);
	const [pokemonDetail, setPokemonDetail] = useState(null);
	const [isLoading, setIsLoading] = useState(false);
	const url = "https://pokeapi.co/api/v2/pokemon?limit=250";
	const [pageNumber, setPageNumber] = useState(0);
	const pokemonPerPage = 30;
	const pagesVisited = pageNumber * pokemonPerPage;
	const pageCount = Math.ceil(pokemons.length / pokemonPerPage);
	const changePage = ({ selected }) => {
		setPageNumber(selected);
	};
	// const [value, setValue] = useState("");
	// const [sortValue, setSortValue] = useState("");
	// const sortOptions = ["name", "address", "email", "phone", "status"];

	const getOnePokemon = (id) => {
		const onepokemon = pokemons.filter((pokemon) => pokemon.id === id);
		setPokemonDetail(onepokemon);
	};

	const getAllPokemons = () => {
		setTimeout(async () => {
			const response = await axios.get(url);
			const data = response.data;

			function createPokemonObject(results) {
				results.forEach(async (pokemon) => {
					const response = await axios.get(
						`https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
					);
					const data = response.data;
					setPokemons((currentList) => [...currentList, data]);
				});
			}
			createPokemonObject(data.results);
			setIsLoading(false);
		}, 3000);
	};

	useEffect(() => {
		setIsLoading(true);
		getAllPokemons();
	}, []);

	// const handleSort = async (e) => {
	// 	let value = e.target.value;
	// 	setSortValue(value);
	// 	return await axios
	// 		.get(`http:// localhost:5000/users?_sort=${value}&_order=asc`)
	// 		.then((response) => {
	// 			setData(response.data);
	// 			setValue("");
	// 		})
	// 		.catch((err) => console.log(err));
	// };

	const sorting2 = (a, b) => {
		if (a.name < b.name) {
			return -1;
		}
		if (a.name > b.name) {
			return 1;
		}
		return 0;
	};

	console.log(pokemons);
	console.log(pokemonDetail);

	pokemons.sort((a, b) => a.id - b.id);

	// pokemons.sort(sorting2);

	const customStyles = {
		content: {
			top: "50%",
			left: "50%",
			right: "auto",
			bottom: "auto",
			marginRight: "-50%",
			transform: "translate(-50%, -50%)",
			background: "white",
			color: "#282c34",
			width: "1000px",
			height: "500px",
			padding: "50px",
			borderRadius: "25px",
			display: "flex",
		},
		overlay: { backgroundColor: "transparent", backdropFilter: "blur(7px)" },
	};

	const closeModal = () => {
		setPokemonDetail(null);
	};

	return (
		<div>
			<header className="App-header">
				{isLoading ? (
					<>
						<div className="App">
							<header className="App-header">
								<h1>
									<ReactLoading
										type="spinningBubbles"
										// type="bars"
										height={200}
										width={200}
										color="#282c34"
									/>
								</h1>
							</header>
						</div>
					</>
				) : (
					<div className="pokemon-container">
						<Navbar />
						{/* <h5>Sort By:</h5>
						<select
							style={{ width: "50%", borderRadius: "2px", height: "35px" }}
							onChange={handleSort}
							value={sortValue}
						>
							<option>Please select value</option>
							{sortOptions.map((item, index) => (
								<option value={item} key={index}>
									{item}
								</option>
							))}
						</select> */}

						<div className="all-container">
							{pokemons
								.slice(pagesVisited, pagesVisited + pokemonPerPage)
								.map((pokemon, index) => (
									<PokemonThumb
										getOnePokemon={getOnePokemon}
										key={index}
										id={pokemon.id}
										image={pokemon.sprites.other.dream_world.front_default}
										name={pokemon.name}
										type={pokemon.types[0].type.name}
									/>
								))}
						</div>
						<ReactPaginate
							previousLabel={<HiArrowSmLeft />}
							nextLabel={<HiArrowSmRight />}
							pageCount={pageCount}
							onPageChange={changePage}
							containerClassName={"paginationBttns"}
							previousLinkClassName={"previousBttn"}
							nextLinkClassName={"nextBttn"}
							activeClassName={"paginationActive"}
							activeLinkClassName={"paginationLinkActive"}
						/>
					</div>
				)}
			</header>
			{pokemonDetail && (
				// <Modal
				// 	isOpen={pokemonDetail}
				// 	contentLabel={pokemonDetail?.name || ""}
				// 	onRequestClose={() => {
				// 		setPokemonDetail(null);
				// 	}}
				// 	style={customStyles}
				// >
				// 	<div>
				// 		<div>
				// 			<img
				// 				src={pokemonDetail[0].sprites.other.dream_world.front_default}
				// 				alt=""
				// 			/>
				// 			<p>Stats</p>
				// 		</div>
				// <div>
				// 	<p>bar chart</p>
				// 	<p>download button</p>
				// </div>

				// 		<p>Can you see the pokemon {pokemonDetail[0].name}</p>
				// 	</div>
				// </Modal>

				<div className="modal__overlay">
					<div className="modal__content">
						<div className="modal__upper">
							<img
								src={pokemonDetail[0].sprites.other.dream_world.front_default}
								alt=""
							/>
							<div className="modal__baseStats">
								<h4>Base stats</h4>
								{pokemonDetail[0].stats.map((stat, key) => (
									<div key={key}>
										<strong>{stat.stat.name}</strong>
										<ProgressBar
											animated
											now={stat.base_stat}
											max={255}
											label={stat.base_stat}
										/>
									</div>
								))}
							</div>
							<button className="close-modal-btn" onClick={closeModal}>
								<FaTimes></FaTimes>
							</button>
						</div>
						<div className="modal__lower">
							<BarChart prop={pokemonDetail} />
							<div className="modal__button">
								<button>DOWNLOAD DATA</button>
							</div>
						</div>
					</div>
				</div>
			)}
		</div>
	);
}

export default PokeDex;
