import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";

const BarChart = ({ prop }) => {
	const [pokemonChartDetail, setPokemonChartDetail] = useState({
		labels: prop[0].stats.map((stat) => stat.stat.name),
		datasets: [
			{
				label: "Pokemon stats",
				data: prop.stats.map((stat) => stat.base_stat),
				backgroundColor: "#282c34",
				borderColor: "black",
				borderWidth: 2,
			},
		],
	});
	console.log(pokemonChartDetail);
	return (
		<div>
			<Bar
				data={{
					labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
					datasets: [
						{
							label: "Pokemon stats",
							data: [55, 66, 10, 250, 30, 70],
							backgroundColor: [
								"rgba(255, 99, 132, 0.2)",
								"rgba(54, 162, 235, 0.2)",
								"rgba(255, 206, 86, 0.2)",
								"rgba(75, 192, 192, 0.2)",
								"rgba(153, 102, 255, 0.2)",
								"rgba(255, 159, 64, 0.2)",
							],
							borderColor: [
								"rgba(255, 99, 132, 1)",
								"rgba(54, 162, 235, 1)",
								"rgba(255, 206, 86, 1)",
								"rgba(75, 192, 192, 1)",
								"rgba(153, 102, 255, 1)",
								"rgba(255, 159, 64, 1)",
							],
							borderWidth: 1,
						},
					],
				}}
				height={200}
				width={600}
				options={{
					maintainAspectRatio: false,
					scales: {
						yAxes: [
							{
								ticks: {
									beginAtZero: true,
								},
							},
						],
					},
					legend: {
						labels: {
							fontSize: 25,
						},
					},
				}}
			/>
		</div>
	);
};

export default BarChart;
